package Java_code_example_alone;

public class Ex02_1 {
    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int a, b;
        int result;

        a = 100;
        b = 50;
        System.out.print("코딩연습");

        result = a + b;
        System.out.println(a + "+" + b + "=" + result);

        result = a - b;
        System.out.println(a + "-" + b + "=" + result);

        result = a * b;
        System.out.println(a + "*" + b + "=" + result);

        result = a / b;
        System.out.println(a + "/" + b + "=" + result);
    }
}

